package collection.framework.pure.collection;

public class BinaryStringConversion {

    public static int minOperations(String binaryStr) {
    	
    	if (binaryStr.length() > 100_000) {
            // Handle the case when inputStr is null
            return 0;
        }
    	
        int count = 0;
        char[] chars = binaryStr.toCharArray();

        for (int i = 0; i < chars.length - 1; i++) {
            if (chars[i] != '0') {
                chars[i] = '0';
                chars[i + 1] = (chars[i + 1] == '0') ? '1' : '0';
                count++;
            }
        }

        return count;
    }

    public static void main(String[] args) {
        String binaryStr = "";
        int minOperations = minOperations(binaryStr);
        System.out.println("Minimum operations: " + minOperations);
    }
}
